svg.script:
var animates=["顔の輪郭","鼻1","鼻2","口","目の輪郭1","目の輪郭2","ハイライト1","ハイライト2","帽子の輪郭1","帽子の輪郭2",
"帽子の塗り1","帽子の塗り2","目の塗り1","目の塗り2","顔の塗り"];
(function oneset(interval){
	var fill_table={};
	animates.forEach(function(id){
		var elm=document.getElementById(id);
		//Path
		var lgh=elm.getTotalLength();
		elm.style.transition="none";
		elm.style.strokeDasharray=lgh+" "+lgh;
		elm.style.strokeDashoffset=lgh;
		//fill
		fill_table[id]=elm.style.fill;
		elm.style.fill="none";
		//再描画
		elm.getBoundingClientRect();
		elm.style.transition="stroke-dashoffset "+interval+"ms ease-in-out";
	});
	(function doo(index){
		var id=animates[index];
		if(id==null){
			setTimeout(oneset.bind(null,interval),interval*5);
			return;
		}
		var elm=document.getElementById(id);
		elm.style.strokeDashoffset="0";
		elm.style.fill=fill_table[id];
		//つぎ
		setTimeout(doo.bind(null,index+1),interval);
	})(0);
})(400);
